export default function Services() {
  return (
    <section id="services" className="section !mt-[-55px]">
      <div className="container">
        <SectionHeader
          eyebrow="Capabilities"
          title="Services I Provide"
          subtitle="Focused on building high-quality, modern web applications with React and Next.js."
        />
        <div className="mt-12 grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {items.map((s, idx) => (
            <motion.div
              key={s.title}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ delay: idx * 0.05 }}
              className="card p-6 hover:shadow-lg transition"
            >
              <div className="w-12 h-12 rounded-xl flex items-center justify-center ">
                {s.icon}
              </div>
              <h3 className="mt-5 text-xl font-bold">{s.title}</h3>
              <p className="mt-2 text-ink/70 text-sm">{s.desc}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
